import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
// 1.import from react-redux and redux
import {createStore,combineReducers} from 'redux'
import {Provider} from 'react-redux'
import {composeWithDevTools} from 'redux-devtools-extension'


// 2.create user reducer fuction
const authReducer=(state = {name:"ryan" ,role:"seller"},action) => {//{ type:'LOGGED_IN_USER4',payload:{name:'ryan',role:'seller'}}
  switch(action.type){
    case "LOGGED_IN_USER":
      return {...state, ...action.payload}
      case "LOGOUT":
        return action.payload;
        default:
          return state;
  }
}
// 3.combine multiple reducers

const rootReducer = combineReducers({
  user:authReducer,  
});
// 4.create redux store
const store = createStore(rootReducer,composeWithDevTools());
//5.provide redux store  to the entire app

ReactDOM.render(
  <React.StrictMode>

    <Provider store={store}> 
    <App />
    </Provider>
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
