// 2.create user reducer fuction
 export const authReducer=(state = {name:"ryan" ,role:"seller"},action) => {//{ type:'LOGGED_IN_USER4',payload:{name:'ryan',role:'seller'}}
    switch(action.type){
      case "LOGGED_IN_USER":
        return {...state, ...action.payload}
        case "LOGOUT":
          return action.payload;
          default:
            return state;
    }
  }
  export default authReducer;
